import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import { AddUser } from '../../stores/actions/user';
// import Popup from './Adduserpopup';
import Button from 'react-bootstrap/Button';

const createUser = (props) => {

    const history = useHistory();
    const [id, setId] = useState('');
	const [username, setUsername] = useState('');
    const [phone_no, setPhoneNo] = useState('');
    const [address, setAddress] = useState('');

	const [error, setError] = useState(false);
	const [errorMessage , setErrorMessage] = useState('');
	const [visible , setVisible] = useState("");

	// To  Manage the State in the Component :-
	let { userType} =  useSelector( state => state.User );

	useEffect( () => {
		if(userType === 0){
			history.push("/");
		}
	}, []);

	// Use to dispatch an action to the reducer :-
	const dispatchAction = useDispatch();

	const popupStatus = () => {
		setVisible(!visible);
	}

	const submitUser = (e) => {
		e.preventDefault();
		if(username === "" || address === "" || phone_no === ""){
			setError(true);
			setErrorMsg("Fields are required");
			return false ;
		}
		dispatchAction(AddUser({ id : (new Date()).getTime(), name : username, phone_no : phone_no, address : address }));
		setShow(true);
		setUsername('');	
		setPhoneNo('');
		setAddress('');
		setErrorMsg(true);
	}

	const handleChange = (e, type) => {

		if(type === "username"){
			setUsername(e.target.value);
		}else if(type === "phone_no"){
			setPhoneNo(e.target.value);
		}else if(type === "address"){
			setAddress(e.target.value);
		}
	}

	return (
		<>
			{/* {show && <Popup pop = {popupStatus} />} */}
			<form onSubmit = {submitUser}>
			  <div className="form-group">
                  <div className="row">
                      <lable className="col-md-2">Username</lable>
                      <div className="col-md-9">
                        <input 
                            type="text" 
                            className="form-control" 
                            value = {username} 
                            onChange = {(e) => handleChange(e, 'username') }
                            placeholder="Username"
                        />
                    </div>
                    {error && username === "" && <p className="alert-danger">{errorMsg}</p>}
                  </div>    
			  </div>
			  <div className="form-group">
                  <div className="row">
                    <lable className="col-md-2">Phone No</lable>
			        <div className="col-md-9">
                        <input type="number" 
                        className="form-control"  
                        value = {phone} 
                        onChange = {(e) => handleChange(e, 'phone_no') }
                        placeholder="Phone Number"
                        />
                        {error && phone_no === "" && <p className="alert-danger">{errorMsg}</p>}
                    </div>
                </div>
			  </div>
			  <div className="form-group">
			    <label className="col-md-2">Address</label>
                <div className="col-md-9">
                    <input type="text" 
                        className="form-control"  
                        value = {address} 
                        onChange = {(e) => handleChange(e, "address") }
                        placeholder="Enter Address" 
                        id="pwd" 
                    />
                    {error && address === "" && <p className="alert-danger">{errorMsg}</p>}
                </div>
			  </div>
			  <Button type="submit" variant="primary">Create User</Button>
			</form>
		</>
	)
}

export default createUser;
