import React,{useEffect} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getUserList } from '../../stores/actions/user';
import { useHistory } from "react-router-dom";
import Button from 'react-bootstrap/Button';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const Users = (props) => {
    const history = useHistory();

    let {users, userType} =  useSelector( state => state.User );

    const dispatchActions = useDispatch();

    const getUsers =  () => {
        dispatchActions(getUserList());
   }

   useEffect( () => {
        if(userType === 0){
            history.push("/");
        }
        getUsers();
    }, []);


    const handlePopup = () => {
		props.history.push(`/createUser`);
    }

    const classes = useStyles();

    return (
        <div>
            <div>
                { userType === 1 && <Button className = "btn btn-primary" onClick = {handlePopup}>Add User</Button>}
            </div>
            <div>
                Test
                <Datatables data = {users} type = "userlist" />
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead>
                        <TableRow>
                            <TableCell>Id</TableCell>
                            <TableCell align="right">Username</TableCell>
                            <TableCell align="right">Phone No</TableCell>
                            <TableCell align="right">Role</TableCell>
                        </TableRow>
                        </TableHead>
                        <TableBody>
                        {users.map((row) => (
                            <TableRow key={row.id}>
                            <TableCell align="right">{row.id}</TableCell>
                            <TableCell align="right">{row.username}</TableCell>
                            <TableCell align="right">{row.phone_no}</TableCell>
                            <TableCell align="right">{row.role}</TableCell>
                            </TableRow>
                        ))}
                        </TableBody>
                    </Table>
                    </TableContainer>
            </div>
        </div>

    )
}

export default Users;
