import {
    ADD_USER,
    USER_LIST,
    LOGIN_TYPE
} from './types';

export const loginUserAs = (type) => {
    return (dispatch) => {
        dispatch(asyncLoginAs(type));
    }
}

export const asyncLoginAs = (type) => {
    return {
        type: LOGIN_TYPE,
        addType : type
    }
}

export const getUserList = (userData) => {
    return (dispatch) => {
		dispatch(asyncUserList()); 
	}
}

export const AddUser = (data) => {
    return (dispatch) => {
		dispatch(asyncAddUser(data, data.addType));
	}
}

export const asyncAddUser = (userData, addType) => {
    return{
        loading :  false,
        payload : userData,
        type    : ADD_USER,
        addType : addType
    }
}

export const asyncUserList = () => {
    return {
		type    : USER_LIST,
		loading : false,
	}
}
