import React,{useState} from 'react';
import Button from 'react-bootstrap/Button'

const Login = (props) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [type, setUserType] =  useState('')
    const [error, setError] = useState('');

    const handleChange = (event, type) => {
        console.log(event.target.value);

        if(type === 'username' && '' !== event.target.value) {
            setUsername(event.target.value);
        } else if(type === 'password' && '' !== event.target.value) {
            setPassword(event.target.value);
        }

        if(type === 'username' && '' === event.target.value) {
            setError('Username is requried.');
        }

        if(type === 'password' && '' === event.target.value) {
            setError('Password is required.');
        }
    }

    const handleRadio = (event) => {
        if('' !== event.target.value) {
            setError('Please select user type');
        } else {
            setUserType();
        }
    }
    const handleSubmit = () => {

    }
    return (
        <form className="form" onSubmit={handleSubmit}>
            <div className="form-group">
                <div className="row">
                    <lable className="form-label">Username</lable>
                    <div className="col-md-12">
                        <input type="text" name="username" onChange={(event) => handleChange(event, 'username')} className="form-control"/>
                        {
                            error ? '<p>'+error+'</p>':''
                        }
                    </div>
                </div>
            </div>
            <div className="form-group ">
                <div className="row">
                    <lable className="form-label">Password</lable>
                    <div className="col-md-12">
                        <input type="password" name="password" onChange={(event) => handleChange(event, 'password')} className="form-control"/>
                        {
                            error ? '<p>'+error+'</p>':''
                        }
                    </div>
                </div>
            </div>
            <div className="form-group">
                <div className="row">
                    <input type="radio" name="userType" className="form-control" value="Admin" onClick={(event) => handleRadio(event)}/>Admin
                    <input type="radio" name="userType" className="form-control" value="User" onClick={(event) => handleRadio(event)}/>User
                </div>
            </div>
            <div className="form-group row">
                <div>
                    <Button variant="primary">Login</Button>
                </div>
            </div>
        </form>
    )
}

export default Login;
