import React,{useState} from 'react';
import Button from 'react-bootstrap/Button'

const Login = (props) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [type, setUserType] =  useState('')
    const [error, setError] = useState('');

    const handleChange = (event, type) => {
        if(type === 'username' && '' !== event.target.value) {
            setUsername(event.target.value);
        } else if(type === 'password' && '' !== event.target.value) {
            setPassword(event.target.value);
        } else if( type === 'admin') {
            setUserType(1);
        } else if( type === 'user') {
            setUserType(2);
        }

    }

    const handleSubmit = () => {

    }
    return (
        <form className="form" onSubmit={handleSubmit}>
            <div className="form-group">
                <div className="row">
                    <lable className="form-label">Username</lable>
                    <div className="col-md-12">
                        <input type="text" name="username" onChange={(event) => handleChange(event, 'username')} className="form-control"/>
                        {
                            error ? '<p>'+error+'</p>':''
                        }
                    </div>
                </div>
            </div>
            <div className="form-group ">
                <div className="row">
                    <lable className="form-label">Password</lable>
                    <div className="col-md-12">
                        <input type="password" name="password" onChange={(event) => handleChange(event, 'password')} className="form-control"/>
                        {
                            error ? '<p>'+error+'</p>':''
                        }
                    </div>
                </div>
            </div>
            <div className="form-group">
                <div className="row">
                    <input type="radio" name="userType" className="form-control" onClick={(event) => handleChange(event,'admin')}/>Admin
                    <input type="radio" name="userType" className="form-control" onClick={(event) => handleChange(event,'user')}/>User
                </div>
            </div>
            <div className="form-group row">
                <div>
                    <Button variant="primary">Login</Button>
                </div>
            </div>
        </form>
    )
}

export default Login;
