import React,{useEffect} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getUser } from '../../stores/actions/action';
import { useHistory } from "react-router-dom";

const Users = (props) => {
    const history = useHistory();

    let {users, userType} =  useSelector( state => state.User );

    const dispatchActions = useDispatch();

    const getUsers =  () => {
        dispatchActions(getUser());
   }

   useEffect( () => {
        if(userType === 0){
            history.push("/");
        }
        getUsers();
    }, []);


    const handlePopup = () => {
		props.history.push(`/createUser`);
    }

    return (
        <div>
            <div>
                { userType === 1 && <Button className = "btn btn-primary" onClick = {handlePopup}>Add User</Button>}
            </div>
            <div>
                {/* <Datatables data = {users} type = "userlist" /> */}
            </div>
        </div>

    )
}

export default Users;
